$q_stockopts = "SELECT option_id FROM "
            .$glob['dbprefix']."CubeCart_options_stock_opts "
            ."WHERE product_id = '%d' ORDER BY option_id ASC";

$q_prodopts = "SELECT DISTINCT bot.option_id, top.option_name "
            ."FROM ".$glob['dbprefix']."CubeCart_options_bot AS bot, "
            .$glob['dbprefix']."CubeCart_options_top AS top "
            ."WHERE bot.option_id = top.option_id AND bot.product = "
            ."'%d' ORDER BY bot.option_id ASC, bot.value_id ASC";

$q_optvalues = "SELECT DISTINCT bot.assign_id, value_name FROM "
            .$glob['dbprefix']."CubeCart_options_mid AS mid, "
            .$glob['dbprefix']."CubeCart_options_bot AS bot "
            ."WHERE mid.value_id = bot.value_id AND bot.product = "
            ."'%d' ORDER BY bot.assign_id ASC";

$q_prodattr = "SELECT DISTINCT bot.option_id, bot.assign_id "
            ."FROM ".$glob['dbprefix']."CubeCart_options_bot AS bot, "
            .$glob['dbprefix']."CubeCart_options_top AS top "
            ."WHERE bot.option_id = top.option_id AND bot.product = "
            ."'%d' ORDER BY bot.option_id ASC, bot.value_id ASC";

$q_assignids = "SELECT DISTINCT bot.assign_id, bot.product, bot.option_id "
            ."FROM ".$glob['dbprefix']."CubeCart_options_bot AS bot, "
            .$glob['dbprefix']."CubeCart_options_stock_opts AS opts "
            ."WHERE bot.product = opts.product_id "
            ."AND bot.option_id = opts.option_id AND bot.product = "
            ."'%d' ORDER BY bot.option_id ASC";

$q_stocklevels = "SELECT stock_level, product_code, assign_key FROM ".$glob['dbprefix']
            ."CubeCart_options_stock_levels WHERE assign_key = '%s'";

$q_prodinfo = "SELECT stock_level, useStockLevel, productCode FROM "
            .$glob['dbprefix']."CubeCart_inventory WHERE productId = '%d'";

$q_allstocklevels = "SELECT assign_key, product_code, stock_level FROM "
